import { model } from 'mongoose';
import {useRouter} from 'next/router'
import { useRef,useEffect}from 'react'
import baseURL from '../../helper/baseURL';
import Link from 'next/link';
const product=(props)=>{
    const router=useRouter()
    const modalRef=useRef(null);
    useEffect(()=>{
        M.Modal.init(modalRef.current);
        //here modal agar triiger p work krana toh initialise karna padata h uske refrence se jo tiriger k bad display hoga
    },[])
    if(router.isFallback){
        //agar use static props krte and and fallback true h then agar wo page already build nhi h toh server se fetch
        //krenge jisme time lgta h toh tb tak ham loading a option dekha sakte h.
        return(<h1>...Loading</h1>)
    }
    // console.log(props);
    const getModal=()=>{
        return(
            <div id="modal1" className="modal" ref={modalRef}> 
            <div className="modal-content">
              <h4>Modal Header</h4>
              <p>Are u sure to delete this item</p>
            </div>
            <button onClick={()=>deleteProduct()} className="waves-effect waves-light btn #d50000 red accent-4" style={{margin:"5px"}}><b>Delete</b> <i className="material-icons">delete</i></button>
            <button className="waves-effect waves-light btn #4a148c purple darken-4"><b>Cancel</b> <i className="material-icons">add</i></button>
 
          </div>
        )
    }
    const deleteProduct=()=>{
        console.log("del method");
        console.log(props.value._id);
        fetch(`${baseURL}/product/${props.value._id}`,{
            method:'DELETE'
        }).then(res=>res.json()).then(result=>console.log("deleted"));
        router.push('/');
    }
    return(
    <div className="container center-align " style={{marginBottom:"20px"}}>
        <h2>{props.value.name}</h2>
     <img src={props.value.mediaURL} style={{"width":"35%", "height":"400px"}}/>
   <b><p>RS {props.value.price}</p></b>
    <input type="number" style={{width:"50%",textAlign:"center"}} min="1" placeholder="Quantity" />
   <button className="waves-effect waves-light btn"><b>Add</b> <i className="material-icons">+</i></button>
        <p>{props.value.description}</p>
        <button data-target="modal1" className="waves-effect waves-light btn #d50000 red accent-4 modal-trigger"><b>Delete</b> <i className="material-icons">delete</i></button>  
        {getModal()}
        
    {/* modal trigger lgana jarurui hota h also modal target btana bhi aur initalise krna bhi tbi modal work krega */}
    </div>
)
}
