const product=(props)=>{
    console.log(props);
return(
    <div class="row">
    <div class="card small ">
      <div class="card-image">
        <img src={props.value.mediaURL}/>
        <span class="card-title">{props.value.name}</span>
      </div>
      <div class="card-content">
        <p>{props.value.description}</p>
      </div>
      <div class="card-action">
        <a href="#">This is a link</a>
      </div>
    </div>
    </div>
)
}
export async function getServerSideProps(context) {
   
   const pid= context.params.id;
   const data=await fetch(`http://localhost:3000/api/product/${pid}`).then(res=>res.json());

   console.log(data);
   return{
       props:{
           value:data
       }
   }
}       
export default product;
