import {useRouter} from 'next/router'
import baseURL from '../../helper/baseURL';
const product=(props)=>{
    const router=useRouter()
    if(router.isFallback){
        //agar use static props krte and and fallback true h then agar wo page already build nhi h toh server se fetch
        //krenge jisme time lgta h toh tb tak ham loading a option dekha sakte h.
        return(<h1>...Loading</h1>)
    }
    // console.log(props);
return(
    <div className="container center-align">
        <h2>{props.value.name}</h2>
     <img src={props.value.mediaURL} style={{"width":"35%", "height":"400px"}}/>
   <b><p>RS {props.value.price}</p></b>
    <input type="number" style={{width:"50%",border:"solid 2px" }} min="1" placeholder="Quantity" />
   <button className="waves-effect waves-light btn">Add <i class="material-icons">add</i></button>
        <p>{props.value.description}</p>
 
    </div>
)
}
