import {parseCookies} from 'nookies'
import baseUrl from '../helpers/baseUrl'
const Account=()=>{
    const cookie =parseCookies();
    const user=cookie.user?JSON.parse(cookie.user):""
    return (
        <div className="container">
            <div className="center-align">
                <h4>{user.name}</h4>
                <h4>{user.email}</h4>
