import{useState,useEffect} from 'react'
import {parseCookies} from 'nookies'
import baseURL from '../helper/baseURL'
function UserRoles(){
    const handleRole=async(_id,role)=>{
        await fetch(`${baseURL}/api/users`,{
            headers:{
                "Authorization":token
            },
            method:"PUT",
            body:JSON.stringify({
                _id:_id,
                role:role
            })
        });
    }
    const[user,setuser]=useState([]);
    useEffect(()=>{
        fetchUser();
    },[])
    const {token}=parseCookies()
    const fetchUser=async()=>{
       const res= await fetch(`${baseURL}/api/users`,{  
            headers:{
                "Authorization":token
            }
        })
        const res2=await res.json();
        console.log(res2);
        setuser(res2);
    }
    return(
         <div>
           
      <table>
        <thead>
          <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
          </tr>
        </thead>

        <tbody>
            {
         user.map((item)=>{
        return(
            //alt+shift+downkey is used to copy the current line to the next line
             <tr key={item._id}>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>{item.role}<button onClick={handleRole(item._id,item.role)}>Toggle role</button></td>
            </tr>
              )})
            }
          
        </tbody>
      </table>
                 
        </div>

    )
   
  
}
export default UserRoles;
