import {useState} from 'react'
import Link from 'next/link'
import baseURL from '../helper/baseURL'
const login=()=>{
  const [email,setemail]=useState();
  const[password,setpassword]=useState();
  const UserLogin=async(e)=>{
    e.preventDefault();
    const res=await fetch(`${baseURL}/api/login`,{
      method:"POST",
      headers:{
        'Content-Type':'application/json'
      },
      body:JSON.stringify({
        email:email,
        password:password
      })
    })
    const res2=res.json();
    if(res2.error){
      M.toast({html: res2.error,classes:"red"})
    }
    else{

    }
  }
    return( <>
    <form onSubmit={(e)=>UserLogin(e)}>
    <div className=" container center-align card authcard">
    <h1>Login</h1>
      <input type="text" placeholder="Email" value={email} onChange={(e)=>{
        setemail(e.target.value);
      }}/><input type="text" placeholder="password" value={password} onChange={(e)=>{
        setpassword(e.target.value);
      }}/>
         <button type="submit" className="waves-effect waves-light btn "><i className="material-icons left ">forward</i>Login</button>
         <div>
      <button style={{margin:"8px" }} className="waves-effect waves-light btn">
      <Link href="/login"><a><h5 >Does not have account</h5></a></Link>
      </button>
      </div>

    </div>
    </form>
    </>
    )
}
export default login
