import Product from '../../../models/Product'
export default async(req,res)=>{
    const {pid}=req.query;
 
    switch(req.method){
        case "GET": 
          // console.log(pid);
        Product.findOne({_id:pid},(err,result)=>{
        if(err){
            console.log(err);
        }
        else{
            res.status(200).json(result);
        }
    });
    break;
        case "DELETE":
        Product.findOneAndRemove({_id:pid},(error)=>{
            if(!error){
                console.log("deleted")
            }
            else{
                console.log(error);
            }
        })
        break;    
    }
    // const pid=req.query.pid;another way for doing this can be
    
}
