const product=({value})=>{
    console.log(value);
return(
    <h1>{value.name}</h1>
)
}
export async function getServerSideProps(context) {
   
    const id=context.params.id;
    const data=fetch(`http://localhost:3000/api/product/${id}`).then(res=>res.json()).then(result=>result);
 return{
     props:{value:data}
 }
}       
export default product;
