import {useState} from 'react';
const create=()=>{
    const [name,setName]=useState("")
    const [place,setPrice]=useState("")
    const[media,setMedia]=useState("")
    const [description,setDescription]=useState("")
    const handleSubmit=(e)=>{
  e.preventDefault();
    }
    return(
    <form className="container" onSubmit={()=>handleSubmit()}>
        <input type="text" placeholder="Enter Name"   value={name} onChange={(e)=>{
            console.log(e.target.value);    
         setName(()=>e.target.value);
        }}/>
        <input type="text" value={place} placeholder="Enter Price" onChange={(e)=>{
            setPrice(()=>e.target.value)
        }}/>
       
        <div className="file-field input-field">
      <div className="btn">
        <span>File</span>
        <input type="file" placeholder="Choose" onChange={(e)=>{setMedia(()=>e.target.files[0])}}/>
      </div>
      <div className="file-path-wrapper">
        <input className="file-path validate" type="text"/>
      </div>
    </div>
    <textarea  value={description} placeholder={"add some description"} className="materialize-textarea" onChange={(e)=>setDescription(()=>e.target.value)}></textarea>
    <button type="submit" className="waves-effect waves-light btn"><i className="material-icons left">send</i></button>
      </form>
    )
}
export default create
