import jwt from 'jsonwebtoken'
import Cart from '../../models/Cart'
export default async(req,res)=>{
    switch(req.method){
        case "GET":
            await fetchUserCart(req,res)
            break
        case "PUT":
            await addProduct(req,res)
            break 
    }
}
function Authenticated(icomponent){
     return (req,res)=>{
        const {authorization}=req.headers;
        if(!authorization){
            return res.status(401).json({error:"you must logged in"})
        }
       try {
                const {userid}=jwt.verify(authorization,process.env.JWT_SECRET);
                console.log(userid+"sa");
                req.userid=userid
                return icomponent(req,res)
           }
       catch (error) {
                return  res.status(401).json({error:"login please"})
            }
    }
}
    const fetchUserCart=Authenticated(async(req,res)=>{
       
            const cart=await Cart.findOne({user:userid})
            res.status(200).json(cart.products)
    })
    
    const addProduct=Authenticated(async(req,res)=>{
        const {quantity,productId}=req.body
        const cart=await Cart.findOne({user:req.userid})
        const pExist=Cart.products.some(pdoc=>productId===pdoc.product.totring())
        if(pExist){
            await Cart.findOneAndUpdate({_id:cart._id,"products.product":productId},{$inc:{"products.$.quantity":quantity}})
        }
        else{
            const newProduct={quantity:quantity,product:productid}
        await Cart.findOneAndUpdate({_id:cart._id},
            {$push:{products:newProduct}})
        }
        res.status(200).json({message:"product added to cart"})
    } )
