import mongoose from 'mongoose';
const {
  ObjectId
} = mongoose.Schema.Types;
const cartSchema = new mongoose.Schema({
  user: {
    type: ObjectId,
    ref: "User" //har ek card se associated ek user hoga

  },
  products: [{
    quantity: {
      type: Number,
      default: 1
    },
    product: {
      type: ObjectId,
      ref: "product"
    }
  }]
});
export default mongoose.models.Cart || mongoose.model("Cart", cartSchema); //  
