// so seo friendly kyki insepect element me u can see the loaded content
import Link from 'next/link';
import {useState,useEffect} from 'react';
import Navbar from '../components/NavBar'
//get static props is used when we use the page that is not user specific like website kholte hi jo items ka page show
//hoga wo user ki reqauest se pehle hi build krke rakhte h .wo tab hoga jb hm get static props ka use krenge
//if we use getserver side props toh req server p jygi phir server response krega ie cN BE used in user specific page
//toh thoda slow hoga ye.
const Home=(props)=>{
  const productlist=props.products.map(val=>{
    return (
      <div classsName="card" key={val._id}>
      <div classsName="card-image waves-effect waves-block waves-light">
        <img classsName="activator" src={val.mediaURL}/>
      </div>
      <div classsName="card-content">
        <span classsName="card-title activator grey-text text-darken-4">{val.name}<i classsName="material-icons right">more_vert</i></span>
      </div>
      <div classsName="card-reveal">
        <span classsName="card-title grey-text text-darken-4">{val.price}<i classsName="material-icons right">close</i></span>
        <p>Here is some more information about this product that is only revealed once clicked on.</p>
      </div>
    </div>   
    )
  })
  console.log(props)
  // const [text,setdata]=useState('..load');
  // useEffect(()=>{
  //   fetch('http://localhost:3000/api/test').then(res=>res.json()).then(data=>{

  //     setdata(data.message);
  //   })
  // },[])
return (
  <>
  <h1>next Js provide the feature of inbuilt JSX</h1>
  {productlist}
  {/* <h2>{props.message}</h2> */}
  {/* <style jsx>
    {
      `h2{
      color:red
      }`
    }
  </style> */}
  <Link href='/product'><a>Go to Products</a></Link>
  </>
)
}
export async function getStaticProps(context){
  const data= await fetch('http://localhost:3000/api/products').then(res=>res.json());

  return{
    props:{
       products:data
    }
  }
}

