import Product from '../../../models/Product'
export default (req,res)=>{
    // const pid=req.query.pid;another way for doing this can be
    const {pid}=req.query;
    console.log(pid);
    Product.findOne({_id:pid},(err,result)=>{

        if(err){
            console.log(err);
        }
        else{
            res.status(200).json(result);
        }
        
    });
    
}
