import {useRouter} from 'next/router'
import { useRef,useEffect}from 'react'
import baseURL from '../../helper/baseURL';
const product=(props)=>{
    const router=useRouter()
    const modalRef=useRef(null);
    
    if(router.isFallback){
        //agar use static props krte and and fallback true h then agar wo page already build nhi h toh server se fetch
        //krenge jisme time lgta h toh tb tak ham loading a option dekha sakte h.
        return(<h1>...Loading</h1>)
    }
    // console.log(props);
    const getModel=()=>{
        return(
            <div id="modal1" class="modal" ref={modalRef}> 
            <div class="modal-content">
              <h4>Modal Header</h4>
              <p>A bunch of text</p>
            </div>
            <div class="modal-footer">
              <a href="#!" class="modal-close waves-effect waves-green btn-flat">Are u sure to delete this item</a>
            </div>
          </div>
        )
    }
    return(
    <div className="container center-align " style={{marginBottom:"20px"}}>
        <h2>{props.value.name}</h2>
     <img src={props.value.mediaURL} style={{"width":"35%", "height":"400px"}}/>
   <b><p>RS {props.value.price}</p></b>
    <input type="number" style={{width:"50%",textAlign:"center"}} min="1" placeholder="Quantity" />
   <button className="waves-effect waves-light btn"><b>Add</b> <i class="material-icons">+</i></button>
        <p>{props.value.description}</p>
        <button className="waves-effect waves-light btn #d50000 red accent-4 modal-trigger"><b>Delete</b> <i class="material-icons">delete</i></button>  
 {getModel()}
    </div>
)
}
