const product=(props)=>{
return(
    <div class="row">
    <div class="card small ">
      <div class="card-image">
        <img src={props.value.mediaURL}/>
        <span class="card-title">{props.value.name}</span>
      </div>
      <div class="card-content">
        <p>{props.value.description}</p>
      </div>
      <div class="card-action">
        <a href="#">This is a link</a>
      </div>
    </div>
    </div>
)
}
export async function getServerSideProps({paramas:{id}}) {
    const data=await fetch(`http://localhost:3000/api/product/${id}`).then(res=>res.json()).then(result=>result);
  return {
    props: {value:data}, // will be passed to the page component as props
  }
}       
export default product;
